mapper.select:
select
count(1)
from houseinfo

  title = #{title}

    and monthlyRent = #{monthlyRent}


    and payType = #{payType}


    and rentType = #{rentType}


    and roomType = #{roomType}


    and area = #{area}


    and floor = #{floor}


    and addr = #{addr}


    and releaseDate = #{releaseDate}


    and faceTo = #{faceTo}

select

from houseinfo

  1=1

    and monthlyRent LIKE "%"#{monthlyRent}"%"


    and payType = #{payType,jdbcType=VARCHAR}


    and rentType = #{rentType,jdbcType=VARCHAR}


    and roomType = #{roomType,jdbcType=VARCHAR}


    and area = #{area,jdbcType=VARCHAR}


    and floor = #{floor,jdbcType=VARCHAR}


    and addr = #{addr,jdbcType=VARCHAR}


    and decoration = #{decoration,jdbcType=VARCHAR}


    and releaseDate = #{releaseDate,jdbcType=VARCHAR}



    and faceTo = #{faceTo,jdbcType=VARCHAR}


    and source = #{source}
